CREATE VIEW CUST_DETAILS_MINI_VW  (     CUST_ID,     SURNAME,     TMSTAMP,     C_DIGIT,     ENTRY_STATUS,     SHORT_NAME,     CUST_TYPE,     OLD_CUST_ID,     VIP_IND,     DATE_OF_BIRTH,     BLACKLISTED_IND,     EXPIRE_DATE,     NON_REGISTERED,     CUST_STATUS,     TITLE,     TELEX,     SEX,     NON_RESIDENT,     LATIN_SURNAME,     LATIN_FIRSTNAME,     FATHER_SURNAME,     E_MAIL,     NO_AFM,     EMPLOYER,     SECOND_SURNAME,     TELEX_CONNECTION,     DOC_EXPIRE_DATE,     CLEANESS,     CLEANESS_COMMENTS,     CUSTOMER_BEGIN_DAT,     CORRESPONDENT_LIMI,     ALERT_MSG,     LEGAL_EXPIRE_DATE,     SWIFT_CONNECTION_I,     NOSTRO_ACCOUNT_IND,     VOSTRO_ACCOUNT_IND,     EMPLOYEMENT_START,     CHAMBER_ID,     NUM_OF_CHILDREN,     FAMILY_MEMBERS,     PROHIBIT_WITHDRAW,     ATTRACTION_PERSON,     NON_PROFIT,     FKUNIT_BELONGS,     FKUNIT_IS_MONITORE,     FKCURR_HAS_AS_LIMI,     FKCURR_THINKS_IN,     FKUNIT_IS_SERVICED,     FK_DISTR_CHANNEID,     CONSOLID_STATM_FLG,     PENSIONER_IND,     INSTITUTE_INV_IND,     INCOMPLETE_U_COMNT,     LAST_UPDATE,     FK_GLG_ACCOUNTACCO,     FIRST_NAME,     SELF_NUM,     SELF_NAME,     SELF_INDICATOR,     BIRTHPLACE,     NON_RESIDENT_FOR_R,     EMPLOYER_ADDRESS,     CHILDREN_ABOVE18,     FAX_INDICATOR,     LIMIT,     MAJOR_BENEFICIARY,     FK_CUST_BANKEMPID,     FK_BANKEMPLOYEEID,     FK0BANKEMPLOYEEID,     MOBILE_TEL,     ATTRACTION_DETAILS,     SPOUSE_NAME,     TELEPHONE_1,     MIDDLE_NAME,     MARKETING_REMINDER,     MOTHER_NAME,     MOTHER_SURNAME,     ENTRY_COMMENTS,     CHAMBER_COMMENTS,     PROMOCODE,     INTERNET_ADDRESS,     SWIFT_ADDRESS,     FATHER_NAME,     BUSINESS_IND,     MAIL_IND,     STATUS_DATE,     FK_BRANCH_PORTFBRA,     FK_BRANCH_PORTFPOR,     FK_BISS_CODE,     DAI_NUMBER,     SPM_NUMBER,     SEGM_FLAGS,     CERTIFIC_DATE,     CERTIFIC_CUST,     SEPA_AGR_FLG,     SEPA_AGR_DT,     FIN_RANGE,     FIN_RANGE_DT,     FKGD_HAS_COUNTRY,     FKGH_HAS_COUNTRY,     FAX_NO,     C_CITY,     C_ZIP_CODE,     C_TELEPHONE,     C_ADDRESS_1,     C_ADDRESS_2,     C_REGION  )  AS     WITH c_address          AS (SELECT *                FROM cust_address               WHERE     cust_address.communication_addr = '1'                     AND entry_status = '1')     SELECT customer.cust_id,            customer.surname,            customer.tmstamp,            customer.c_digit,            customer.entry_status,            customer.short_name,            customer.cust_type,            customer.old_cust_id,            customer.vip_ind,            customer.date_of_birth,            customer.blacklisted_ind,            customer.expire_date,            customer.non_registered,            customer.cust_status,            customer.title,            customer.telex,            customer.sex,            customer.non_resident,            customer.latin_surname,            customer.latin_firstname,            customer.father_surname,            customer.e_mail,            customer.no_afm,            customer.employer,            customer.second_surname,            customer.telex_connection,            customer.doc_expire_date,            customer.cleaness,            customer.cleaness_comments,            customer.customer_begin_dat,            customer.correspondent_limi,            customer.alert_msg,            customer.legal_expire_date,            customer.swift_connection_i,            customer.nostro_account_ind,            customer.vostro_account_ind,            customer.employement_start,            customer.chamber_id,            customer.num_of_children,            customer.family_members,            customer.prohibit_withdraw,            customer.attraction_person,            customer.non_profit,            customer.fkunit_belongs,            customer.fkunit_is_monitore,            customer.fkcurr_has_as_limi,            customer.fkcurr_thinks_in,            customer.fkunit_is_serviced,            customer.fk_distr_channeid,            customer.consolid_statm_flg,            customer.pensioner_ind,            customer.institute_inv_ind,            customer.incomplete_u_comnt,            customer.last_update,            customer.fk_glg_accountacco,            customer.first_name,            customer.self_num,            customer.self_name,            customer.self_indicator,            customer.birthplace,            customer.non_resident_for_r,            customer.employer_address,            customer.children_above18,            customer.fax_indicator,            customer."LIMIT",            customer.major_beneficiary,            customer.fk_cust_bankempid,            customer.fk_bankemployeeid,            customer.fk0bankemployeeid,            customer.mobile_tel,            customer.attraction_details,            customer.spouse_name,            customer.telephone_1,            customer.middle_name,            customer.marketing_reminder,            customer.mother_name,            customer.mother_surname,            customer.entry_comments,            customer.chamber_comments,            customer.promocode,            customer.internet_address,            customer.swift_address,            customer.father_name,            customer.business_ind,            customer.mail_ind,            customer.status_date,            customer.fk_branch_portfbra,            customer.fk_branch_portfpor,            customer.fk_biss_code,            customer.dai_number,            customer.spm_number,            customer.segm_flags,            customer.certific_date,            customer.certific_cust,            customer.sepa_agr_flg,            customer.sepa_agr_dt,            customer.fin_range,            customer.fin_range_dt,            c_address.fkgd_has_country,            c_address.fkgh_has_country,            c_address.fax_no,            c_address.city c_city,            c_address.zip_code c_zip_code,            c_address.telephone c_telephone,            c_address.address_1 c_address_1,            c_address.address_2 c_address_2,            c_address.region c_region  --     FROM customer, c_address
--    WHERE c_address.fk_customercust_id(+) = customer.cust_id;
     FROM customer       LEFT OUTER JOIN c_address ON c_address.fk_customercust_id = customer.cust_id;

