CREATE VIEW DEP_EXTRAI_VW  (     DEP_ACC_NUMBER,     ENTRY_SER_NUM,     TRANS_SER_NUM,     TRX_DATE,     DEP_PROD_ID,     ID_JUSTIFIC,     ID_TRANSACT,     CURRENCY_ID,     DEBIT_CREDIT_FLAG,     ENTRY_COMMENTS,     TRX_UNIT,     TRX_USR,     TRX_SN,     VALUE_DATE,     CUST_ID,     ENTRY_AMOUNT  )  AS     SELECT dep.account_number dep_acc_number,            entry_ser_num,            trans_ser_num,            trx_date,            dep.fk_depositfk_produ dep_prod_id,            id_justific,            id_transact,            fk_currencyid_curr currency_id,            debit_credit_flag,            entry_comments,            trx_unit,            trx_usr,            trx_sn,            value_date,            fk_customercust_id cust_id,            entry_amount       FROM history_fd_extrait, deposit_account dep      WHERE dep.account_number = history_fd_extrait.account_number     UNION ALL     SELECT dep.account_number dep_acc_number,            trans_ser_num,            entry_ser_num,            trx_date,            dep.fk_depositfk_produ dep_prod_id,            id_justific,            id_transact,            fk_currencyid_curr currency_id,            debit_credit_flag,            entry_comments,            trx_unit,            trx_usr,            trx_sn,            value_date,            dep.fk_customercust_id,            entry_amount       FROM time_depos_extrait, deposit_account dep                          --,
    WHERE     time_depos_extrait.particip_bal_flag = 1            AND dep.account_number = time_depos_extrait.fk_deposit_accoacc;

