CREATE VIEW DEP_SUMTERM  (     ACCOUNT_NUMBER,     ACCOUNT_CD,     CUST_ID,     C_DIGIT,     MOVEMENT_CURRENCY,     PRODUCT_ID,     DAYS_DURATION,     FK_GENERIC_DETASER,     FKGD_CATEGORY,     BOOK_BALANCE,     ACCR_CR_INTEREST,     DB_INT,     N128,     CR_INT_GL_ACC,     CR_INT_ACCR_GL_ACC,     GL_ACCOUNT  )  AS       SELECT * FROM(       SELECT DISTINCT              PROFITS_ACCOUNT.ACCOUNT_NUMBER,              PROFITS_ACCOUNT.ACCOUNT_CD,              PROFITS_ACCOUNT.CUST_ID,              PROFITS_ACCOUNT.C_DIGIT,              PROFITS_ACCOUNT.MOVEMENT_CURRENCY,              PROFITS_ACCOUNT.PRODUCT_ID,              TIME_DEPOS_RENEWAL.DAYS_DURATION,              DEPOSIT_ACCOUNT.FK_GENERIC_DETASER,              DEPOSIT_ACCOUNT.FKGD_CATEGORY,              DEPOSIT_ACCOUNT.BOOK_BALANCE,              DEPOSIT_ACCOUNT.ACCR_CR_INTEREST,                NVL (DEPOSIT_ACCOUNT.ACCR_DB_INTEREST, 0)              - NVL (DEPOSIT_ACCOUNT.DB_PROGRESS_INTER, 0)                 DB_INT,                NVL (DEPOSIT_ACCOUNT.ACCR_N128_INTER, 0)              - NVL (DEPOSIT_ACCOUNT.ACCR_N128_PROGESS, 0)                 N128,              TD_DURATION_GL.CR_INT_GL_ACC,              TD_DURATION_GL.CR_INT_ACCR_GL_ACC,              TD_DURATION_GL.GL_ACCOUNT         FROM DEPOSIT_ACCOUNT DEPOSIT_ACCOUNT,              PROFITS_ACCOUNT PROFITS_ACCOUNT,              TD_DURATION_GL TD_DURATION_GL,              time_depos_renewal time_depos_renewal        WHERE     PROFITS_ACCOUNT.DEP_ACC_NUMBER = DEPOSIT_ACCOUNT.ACCOUNT_NUMBER              AND DEPOSIT_ACCOUNT.ACCOUNT_NUMBER =                     TIME_DEPOS_RENEWAL.FK_DEPOSIT_ACCOACC              AND DEPOSIT_ACCOUNT.ENTRY_STATUS != '0'              AND DEPOSIT_ACCOUNT.ENTRY_STATUS != '3'              AND DEPOSIT_ACCOUNT.ENTRY_STATUS != '4'              AND DEPOSIT_ACCOUNT.RENEWAL_NUMBER =                     TIME_DEPOS_RENEWAL.RENEWAL_SER_NUM              AND DEPOSIT_ACCOUNT.FK_DEPOSITFK_PRODU =                     TD_DURATION_GL.FK_DEPOSITFK_PRODU              AND DEPOSIT_ACCOUNT.FKGD_CATEGORY =                     TD_DURATION_GL.FK_GENERIC_DETASER              AND (    (DEPOSIT_ACCOUNT.DEPOSIT_TYPE = '2')                   AND (PROFITS_ACCOUNT.PRFT_SYSTEM = 3))     ORDER BY PROFITS_ACCOUNT.ACCOUNT_NUMBER);

