CREATE VIEW EOM_REAL_ESTATE_PLEDGE_VW  AS  SELECT loans.eom_date          ,loans.account_number          ,pledge.internal_sn pledge_internal_sn          ,pledge.record_type pledge_record_type          ,pledge.collat_dtl_internal_sn pledge_collat_dtl_internal_sn          ,re.propertyid          ,re.fk_customercust_id property_cust_id          ,re.totaladministrativevalue          ,retyp.description retyp_description        FROM   eom_loans loans           INNER JOIN eom_account_collateral ac              ON (    ac.eom_date = loans.eom_date                  AND ac.account_number = loans.account_number                  AND ac.profits_system = loans.prft_system)           INNER JOIN eom_collat_pledge pledge              ON (    ac.fk_collateral_tfk = pledge.used_collat_type                  AND ac.collateral_fk_unitcode = pledge.used_unit                  AND ac.collateral_sn = pledge.used_collat_sn                  AND ac.eom_date = pledge.eom_date)           INNER JOIN eom_real_estate re              ON (    pledge.real_estate_id = re.propertyid                  AND pledge.eom_date = re.eom_date)           LEFT JOIN eom_generic_detail retyp              ON (    loans.eom_date = retyp.eom_date                  AND re.fk_gh_descr = retyp.fk_generic_headpar                  AND re.fk_gd_descr = retyp.serial_num)  WITH NO ROW MOVEMENT;

