CREATE VIEW LOANS_BENEF_AGREEM_VW  (     ACCOUNT_NUMBER,     FK_UNITCODE,     AGR_YEAR,     AGR_SN,     AGR_MEMBERSHIP_SN,     FK_AGREEMENTFK_UNI,     FK_AGREEMENTAGR_YE,     FK_AGREEMENTAGR_SN,     FK_AGREEMENTAGR_ME,     TMSTAMP,     ACCOUNT_CD,     PRFT_SYSTEM,     ACC_UNIT_CODE,     ID_CURRENCY,     ACC_OPEN_DT,     OV_EXP_DT,     CUST_ID,     ID_PRODUCT,     MAIN_BENEF_FLG,     AGR_STATUS,     BENEF_STATUS  )  AS     SELECT b.account_number,            c.fk_unitcode,            c.agr_year,            c.agr_sn,            c.agr_membership_sn,            d.fk_agreementfk_uni,            d.fk_agreementagr_ye,            d.fk_agreementagr_sn,            d.fk_agreementagr_me,            d.tmstamp,            b.account_cd,            b.prft_system,            c.fk_unitcode AS acc_unit_code,            c.fk_currencyid_curr AS id_currency,            c.agr_issue_dt AS acc_open_dt,            c.agr_expiry_dt AS ov_exp_dt,            d.fk_customercust_id AS cust_id,            c.fk_agreement_tyfk AS id_product,            d.main_benef_flg,            c.agr_status,            d.benef_status       FROM profits_account b, agreement c, agreement_benef d      WHERE     b.agr_unit = c.fk_unitcode            AND b.agr_year = c.agr_year            AND b.agr_sn = c.agr_sn            AND b.agr_membership_sn = c.agr_membership_sn            AND b.prft_system = 19            AND c.fk_unitcode = d.fk_agreementfk_uni            AND c.agr_year = d.fk_agreementagr_ye            AND c.agr_sn = d.fk_agreementagr_sn            AND c.agr_membership_sn = d.fk_agreementagr_me  --          AND c.agr_status IN ('2', '3');

