CREATE VIEW PFOLIO_CUSTOMERS_VW  (     CUST_ID,     CUST_C_DIGIT,     CUST_TYPE,     CUST_FULLNAME,     CUST_SURNAME,     CUST_FIRST_NAME,     CUST_FATHER_NAME,     BRANCH,     PORTFOLIO_CODE,     PORTFOLIO_USERCODE,     PORTFOLIO_DESCRIPTION,     USERCODE,     STAFF_EMPLOYEE_ID,     STAFF_NO,     STAFF_LAST_NAME,     STAFF_FIRST_NAME,     FKUCATEG_OWNS_MAIN,     FKUCATEG_OWNS_MAIN_DESC,     FKUCATEG_OWNS_AS_2,     FKUCATEG_OWNS_AS_3  )  AS     SELECT cust_id,            c_digit cust_c_digit,            cust_type,               TRIM (surname)            || ' '            || TRIM (customer.first_name)            || ' '            || TRIM (customer.father_name)               AS cust_fullname,            surname cust_surname,            customer.first_name cust_first_name,            customer.father_name cust_father_name,            branch,            portfolio_code,            branch_portfolio.fk_usrcode portfolio_usercode,            branch_portfolio.description portfolio_description,            usr.code usercode,            e.id staff_employee_id,            e.staff_no,            e.last_name staff_last_name,            e.first_name staff_first_name,            usr.fkucateg_owns_main,            UNIT_CATEGORY_PER.DESCRIPTION AS FKUCATEG_OWNS_MAIN_DESC,            usr.fkucateg_owns_as_2,            usr.fkucateg_owns_as_3       FROM customer            LEFT JOIN            (branch_portfolio             INNER JOIN usr                ON (    usr.code = branch_portfolio.fk_usrcode                    AND usr.rm_ind = 1                    AND branch_portfolio.entry_status = 1)             INNER JOIN UNIT_CATEGORY_PER                ON (UNIT_CATEGORY_PER.ID = USR.FKUCATEG_OWNS_MAIN)             INNER JOIN bankemployee e ON (usr.fk_bankemployeeid = e.id)             INNER JOIN unit ON (usr.fk_unitcode = unit.code))               ON (    customer.fk_branch_portfbra = branch                   AND customer.fk_branch_portfpor = portfolio_code);

