CREATE VIEW WRH_LOAN_MKT  (     UNIT,     ACCOUNT_NUMBER,     ACCOUNT_CD,     CUST_ID,     PRODUCT_ID,     ACC_BAL,     NRM_BAL,     OV_BAL,     NRM_MKT_AMN,     OV_MKT_AMN,     POSITIVE_AMN,     UNCLEAR_AMN,     NRM_ACCRUAL_AMN,     OV_ACCRUAL_AMN,     LOAN_STATUS,     ACC_STATUS  )  AS     SELECT PROFITS_ACCOUNT.LNS_OPEN_UNIT AS UNIT,            PROFITS_ACCOUNT.ACCOUNT_NUMBER AS ACCOUNT_NUMBER,            PROFITS_ACCOUNT.ACCOUNT_CD AS ACCOUNT_CD,            LOAN_ACCOUNT.CUST_ID,            PROFITS_ACCOUNT.PRODUCT_ID,              LOAN_ACCOUNT.NRM_CAP_BAL            + LOAN_ACCOUNT.NRM_RL_INT_BAL            + LOAN_ACCOUNT.NRM_EXP_BAL            + LOAN_ACCOUNT.NRM_COM_BAL            + LOAN_ACCOUNT.OV_CAP_BAL            + LOAN_ACCOUNT.OV_RL_NRM_INT_BAL            + LOAN_ACCOUNT.OV_RL_PNL_INT_BAL            + LOAN_ACCOUNT.OV_EXP_BAL            + LOAN_ACCOUNT.OV_COM_BAL               AS ACC_BAL,              LOAN_ACCOUNT.NRM_CAP_BAL            + LOAN_ACCOUNT.NRM_RL_INT_BAL            + LOAN_ACCOUNT.NRM_EXP_BAL            + LOAN_ACCOUNT.NRM_COM_BAL               AS NRM_BAL,              LOAN_ACCOUNT.OV_CAP_BAL            + LOAN_ACCOUNT.OV_RL_NRM_INT_BAL            + LOAN_ACCOUNT.OV_RL_PNL_INT_BAL            + LOAN_ACCOUNT.OV_EXP_BAL            + LOAN_ACCOUNT.OV_COM_BAL               AS OV_BAL,            LOAN_ACCOUNT.NRM_URL_INT_BAL AS NRM_MKT_AMN,            LOAN_ACCOUNT.OV_URL_NRM_INT_BAL + LOAN_ACCOUNT.OV_URL_PNL_INT_BAL               AS OV_MKT_AMN,            A.POSITIVE_AMN,            A.UNCLEAR_AMN,            A.NRM_ACCRUAL_AMN,            A.OV_ACCRUAL_AMN,            LOAN_ACCOUNT.LOAN_STATUS,            LOAN_ACCOUNT.ACC_STATUS       FROM R_LOAN_ACCOUNT loan_account,            PROFITS_ACCOUNT,            r_LOAN_ACCOUNT_INF A,            bank_parameters P      WHERE     PROFITS_ACCOUNT.LNS_OPEN_UNIT = LOAN_ACCOUNT.FK_UNITCODE            AND PROFITS_ACCOUNT.LNS_SN = LOAN_ACCOUNT.ACC_SN            AND PROFITS_ACCOUNT.LNS_TYPE = LOAN_ACCOUNT.ACC_TYPE            AND PROFITS_ACCOUNT.PRFT_SYSTEM = 4            AND LOAN_ACCOUNT.ACC_STATUS <> '3'            AND PROFITS_ACCOUNT.LNS_TYPE <> 14            AND LOAN_ACCOUNT.FK_UNITCODE = A.FK_LOAN_ACCOUNTFK            AND LOAN_ACCOUNT.ACC_TYPE = A.FK0LOAN_ACCOUNTACC            AND LOAN_ACCOUNT.ACC_SN = A.FK_LOAN_ACCOUNTACC;

